import logo from "./logo.svg";
import React, { Component } from "react";
import "./App.css";
import { connect } from "react-redux";
const mapStateToProps = (state) => state;
const mapDispatchToProps = (dispatch) => ({
  changeWeather: (city) =>
    dispatch(async (dispatch, getState) => {
      try{
        let response = await fetch(
          `api.openweathermap.org/data/2.5/weather?q=${city}&appid=1a944dea54d850b601ad666177b6deb2`,
          {
            method: "GET",
          }
        );
        let data = await response.json();
        let weather = data;
        console.log(weather);
      }catch(e){
        console.log("Fetching is not ok", e)
      }

      dispatch({
        type: "CHANGE_CURRENT_WEATHER_LOCATION",
        payload: weather,
      });
    }),
});

/* api.openweathermap.org/data/2.5/weather?q={city name}&appid={API key}
          Edit <code>src/App.js</code> and save to reload.  */
class App extends Component {
  render() {
    return <button onClick={() => this.props.changeWeather("London")}> CLICK </button>;
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
