import logo from "./logo.svg";
import React, { Component } from "react";
import "./App.css";
import { connect } from "react-redux";
import SearchEngine from "./components/searchEngine";
import Weather from "./components/weather";
import { Row, Col, Button, Container } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.css";
const mapStateToProps = (state) => state;
const mapDispatchToProps = (dispatch) => ({
  changeWeather: (city) =>
    dispatch(async (dispatch, getState) => {
      try {
        let response = await fetch(
          "https://api.openweathermap.org/data/2.5/weather?q=London&appid=1a944dea54d850b601ad666177b6deb2",
          {
            method: "GET",
          }
        );

        let data = await response.json();
        console.log(data);
        let weather = data;
        console.log(weather);
        dispatch({
          type: "CHANGE_CURRENT_WEATHER_LOCATION",
          payload: weather,
        });
      } catch (e) {
        console.log("Fetching is not ok", e);
      }
    }),
});

/* api.openweathermap.org/data/2.5/weather?q={city name}&appid={API key}
          Edit <code>src/App.js</code> and save to reload.  */
class App extends Component {
  render() {
    return (
      <>
        <SearchEngine />
        <Row className="d-flex justify-content-center">
          <img
            className="loadingImage"
            src="https://images.squarespace-cdn.com/content/v1/56c5082327d4bdbfed3e08df/1499374534846-7N674840CWQGGJUPLGCJ/ke17ZwdGBToddI8pDm48kFUOMarxuZXp5VQe99kv-S5Zw-zPPgdn4jUwVcJE1ZvWQUxwkmyExglNqGp0IvTJZUJFbgE-7XRK3dMEBRBhUpy5pawJafjffF7mg6zLGljH3jq9wUblIbjljQH76WTMK1lltOTojsz-Y7mwVpBtoXc/WeatherIconsMonolin_PartlyCloudy.gif"
          />
        </Row>
        {this.props.load.loaded && <Weather />}
        {this.props.load.loadedInvalidCity && (
          <Row className="d-flex justify-content-center">
            <p className="errorMessage">Please enter e real city</p>
          </Row>
        )}
      </>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
