import React, { Component } from "react";
import { Row, Col, Button } from "react-bootstrap";
import "./searchEngine.css";
import { connect } from "react-redux";
const mapStateToProps = (state) => state;
const mapDispatchToProps = (dispatch) => ({
  changeWeather: (city) =>
    dispatch(async (dispatch, getState) => {
      try {
        let response = await fetch(
          `https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=1a944dea54d850b601ad666177b6deb2`,
          {
            method: "GET",
          }
        );

        let data = await response.json();
        console.log(data);
        let weather = data;
        console.log(weather);
        dispatch({
          type: "CHANGE_CURRENT_WEATHER_LOCATION",
          payload: weather,
        });
      } catch (e) {
        console.log("Fetching is not ok", e);
      }
    }),
    loaded:() => {
      dispatch({
        type: "LOADED",
      });
    }
});
class SearchEngine extends Component {
  dynamicTyping = (e) => {
    this.setState({textForSearch:e.currentTarget.value})
  };
  state = {
    textForSearch: "",
  };
  changeWeatherLoading = () => {
    this.props.changeWeather(this.state.textForSearch)
  }
  render() {
    return (
      <>
        <Row className="d-flex justify-content-center">
          <img src="http://cdn.lowgif.com/full/24649d3b53e7a0ff-.gif" />
        </Row>
        <Row className="inputRow d-flex justify-content-center mb-4">
          <input
            type="text"
            className="inputForm"
            placeholder="   Location"
            onChange={(e) => this.dynamicTyping(e)}
          ></input>
          <Button
            onClick={() =>  this.props.changeWeather(this.state.textForSearch)}
          >
            Search
          </Button>
        </Row>
      </>
    );
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(SearchEngine);
