import React, { Component } from "react";
import { Row, Col, Button } from "react-bootstrap";
import "./searchEngine.css";
import { connect } from "react-redux";
const mapStateToProps = (state) => state;
class Weather extends Component {
  state = {
   firstDay:this.props.currentWeather.weather.list[0], 
   secondDay:this.props.currentWeather.weather.list[7], 
   thirdDay:this.props.currentWeather.weather.list[14], 
   fourthDay:this.props.currentWeather.weather.list[21], 
   fifthDay:this.props.currentWeather.weather.list[28], 
  };
  addNextFiveDays = () => {
      this.setState({nextFiveDays:this.state.nextFiveDays.push(this.props.currentWeather.weather.list[0])});
      this.setState({nextFiveDays:this.state.nextFiveDays.push(this.props.currentWeather.weather.list[7])});
      this.setState({nextFiveDays:this.state.nextFiveDays.push(this.props.currentWeather.weather.list[14])});
      this.setState({nextFiveDays:this.state.nextFiveDays.push(this.props.currentWeather.weather.list[21])});
      this.setState({nextFiveDays:this.state.nextFiveDays.push(this.props.currentWeather.weather.list[28])});
  }
 
  render() {
    return (
      <>
   <button onClick  ={() => this.addNextFiveDays()} >CLICK TO FETCH </button>
      </>
    )
  }
}

export default connect(mapStateToProps) (Weather);
